package Chapter02;

public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 3;
		int y = 4;
		System.out.print((x == y) + " ");
		System.out.print((x != y) + " ");
		System.out.print((x > y) + " ");
		System.out.print((x < y) + " ");
		System.out.print((x <= y) + " ");
		
	}

}
