package Chapter02;

public class TypeConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		double f = 0.0;
		
		f = 5 / 4;
		System.out.println(f);
		
		f = (double) 5 / 4;
		System.out.println(f);
		
		i = (int) 1.3 + (int) 1.8;
		System.out.println(i);
	}

}
